			p[++m]=i+1;
			dfs(s-(2*i-1)-(s!=n),i);
			--m;
		}
	}
	if (s>=1+(s!=n)){
		p[++m]=1;
		dfs(s-1-(s!=n),0);
		--m;
	}
}
