int main()
{
    cin >> N >> K >> s;
    facInit(70*3);

    lint ans = 1;
    for (int i=1; i<=K; i++)
    {
        (ans += dfs(vector<int>{i})) %= MOD;
    }
    cout << ans << endl;
}

