bool check() {
    if (!tot) return 0;
    int now1 = 1, now2 = 1, c = 0;
    rep(i, 1, k) {
        if (!(now1 <= tot || a[now2] > 1 || c)) break;
        if (now1 <= tot && s[i] == 'r') ++now1; else
        if (now2 < now1 && a[now2] > 1 && s[i] == 'b') c += a[now2++] - 2;
        else if (c) --c;
    }
    return !(now1 <= tot || a[now2] > 1 || c);
}

void update() {
    LL tmp = 1, num = tot + 1;
    for(int i = 1, j; i <= tot; i = j + 1) {
        for(j = i; a[j + 1] == a[i]; ++j);
        tmp = tmp * c[j][i - 1] % P;
        num += (j - i + 1) * (a[i] * 2 - 1);
    }
    tmp = tmp * c[num + left - 1][num - 1] % P;
    if ((ans += tmp) >= P) ans -= P;
}

void dfs(int mx) {
    if (left < 0) return;
    if (check()) update();

    if (tot) --left;
    ++tot; --left; dfs(a[tot] = 1); ++left;
    rep(i, 2, mx) {
        left -= i * 2 - 3;
        dfs(a[tot] = i);
        left += i * 2 - 3;
    }
    a[tot--] = 0;
    if (tot) ++left;
}

