  	memset(vis,0,sizeof(vis));
  	int cnt=0;
  	int lx=1,rx=0;
  	for(int j=1;j<=k&&cnt<i;j++)
  	  if (str[j]=='r') q[++rx]=j;
  	  else if (lx<=rx) {
  	  	  vis[q[lx++]]=vis[j]=1;
  	  	  lpos[++cnt]=j;
		}
	if (cnt<i) continue;
	int s=0;
	for(int j=1;j<=k;j++)
	  if (!vis[j]&&str[j]=='r') s++;
	int r=1;
	lpos[i+1]=k+1;up[i+1]=0;
	for(int j=0;j<=s;j++) {
		if (j) {
			while (vis[r]||str[r]!='r') r++;
			vis[r]=1;
		}
		for(int t1=i;t1>0;t1--) {
			up[t1]=up[t1+1];
			for(int t2=lpos[t1+1]-1;t2>lpos[t1];t2--)
			  if (!vis[t2]) up[t1]++;
			up[t1]++;
		}
		reverse(up+1,up+i+1);
		dp(n,i,j);
	}
  }
  int ans=0;
  for(int i=1;i<=n;i++) add(ans,sum[i]*C[n-1][i-1]%MOD);
