void search(int len, int last, int sum) {
	if(len>rcnt) return;
	int r=0, nz=0, b=0, a=0;
	while(nz<len&&f[nz]==0) ++nz;
	nz=len-nz;
	for(int i=0; i<k&&(b!=nz||a!=0); ++i) {
		if(s[i]=='r') if(r<len) ++r; else if(a) --a;
		if(s[i]=='b') if(b<r&&b<nz) ++b, a+=f[len-b]-1; else if(a) --a;
	}
	if(b==nz&&a==0) {
		int fsum=len;
		u64 q=fac[len];
		for(int i=0, j; i<len; ++i) {
			fsum+=f[i];
			if(i==0||f[i]!=f[i-1]) j=0;
			q=q*inv[++j]%P;
		}
		ans=(ans+q*c[n+1+2*nz][2*fsum])%P;
		if(sum>=2) {
			int &F=f[len];
			for(F=last; F<=sum; ++F) search(len+1, F, sum-(F?F:1));
		}
	}
}
