inline LL CC(int a,int b)
{
	if(b==0)return a==0;
	return C[a+b-1][b-1];
}
inline LL Solve(int tot,int res)
{
	int bcnt=0,rcnt=0,wcnt=0;
	For(i,1,tot)
	{
		bcnt+=st[i];
		rcnt+=st[i]+1;
	}
	wcnt=tot+1;
	For(i,0,res)f[i]=0;
	For(i,0,res)g[i]=0;
	//printf("r:%d b:%d w:%d\n",rcnt,bcnt,wcnt);
	For(i,0,res)
		For(j,0,res-i)
			Add(f[i+j],CC(i,rcnt)*CC(j,bcnt)%mod);
	//For(i,0,res)printf("%lld ",f[i]);puts("");
	For(i,0,res)
		For(j,0,res-i)
			Add(g[i+j],f[i]*CC(j,wcnt)%mod);
	//For(i,0,res)printf("%lld ",g[i]);puts("");
	//printf("%lld\n",g[res]);
	return g[res];
}
inline void dfs(int cnt,int res,int now,LL val)
{
	if(res-max(now*2-1,1)-1<0)
	{
		if(check(cnt))
		{
			//For(i,1,cnt)printf("%d ",st[i]);
			//printf("res:%d\n",res);
			LL ret=Solve(cnt,res)*fac[cnt]%mod*val%mod;
			//printf("%lld\n",ret);
			Add(ans,ret);
		}
		return;
	}
	dfs(cnt,res,now+1,val);
	For(i,1,res)
	{
		st[cnt+i]=now;
		int l=res-max(now*2-1,1)*i-i;
		if(l>=0)dfs(cnt+i,l,now+1,val*ifac[i]%mod);
		else break;
	}
}
