	rFor(i,n,2)ifac[i-1]=ifac[i]*i%mod;
	C[0][0]=1;
	For(i,1,n)For(j,0,i)C[i][j]=(C[i-1][j]+(j?C[i-1][j-1]:0))%mod;
}
