bool isvalid(int v){
	int len=0;
	for(int i=0;i<v;i++){
		if(i!=0)len++;
		if(group[i]==1){
			len++;
		}else{
			len+=group[i]*2-3;
		}
	}
	if(len>n)return false;
	memset(assign,0,sizeof(assign));
	memset(used,false,sizeof(used));
	int cnt=0;
	for(int i=0;i<k;i++){
		if(cnt==v)break;
		if(str[i]=='r'){
			assign[cnt++]=i;
			used[i]=true;
		}
	}
	if(cnt<v)return false;
	for(int i=0;i<v;i++){
		if(group[i]>=2){
			bool flag=false;
			for(int j=assign[i]+1;j<k;j++){
				if(str[j]=='b' && !used[j]){
					used[j]=true;
					flag=true;
					break;
				}
			}
			if(!flag)return false;
		}
	}
	for(int i=0;i<v;i++){
		if(group[i]>=3){
			int rest=group[i]-2;
			for(int j=assign[i]+1;j<k;j++){
				if(!used[j]){
					used[j]=true;
					rest--;
				}
				if(rest==0)break;
			}
			if(rest!=0)return false;
		}
	}
	return true;
}

ll dp[2][71];

void dfs(int v,int now){
	if(v>0){
		if(!isvalid(v))return;
		memset(dp,0,sizeof(dp));
		int cur=0,nxt=1;
		dp[cur][v-1]=1;
		for(int i=0;i<v;i++){
			for(int j=0;j<=n;j++){
				if(group[i]==1){
					if(j+1<=n){
						dp[nxt][j+1]+=dp[cur][j];
						dp[nxt][j+1]%=MOD;
					}
				}else{
					int pl=(group[i]-1)*2-1;
					if(j+pl<=n){
						dp[nxt][j+pl]+=dp[cur][j];
						dp[nxt][j+pl]%=MOD;
					}
					pl=(group[i]-1)*2;
					if(j+pl<=n){
						dp[nxt][j+pl]+=dp[cur][j]*2;
						dp[nxt][j+pl]%=MOD;
					}
					pl=(group[i]-1)*2+1;
					if(j+pl<=n){
						dp[nxt][j+pl]+=dp[cur][j];
						dp[nxt][j+pl]%=MOD;
					}
				}
			}
			swap(cur,nxt);
			memset(dp[nxt],0,sizeof(dp[nxt]));
		}
		/*
		for(int i=0;i<v;i++){
			printf("%d ",group[i]);
		}
		printf("ok\n");
		*/
		ll pw=1;
		int len=1,rest=v;
		for(int i=1;i<v;i++){
			if(group[i]==group[i-1]){
				len++;
			}else{
				pw=pw*comb[rest][len]%MOD;
				rest-=len;
				len=1;
			}
		}

		for(int i=1;i<=n;i++){
			ll va=(dp[cur][i]*pw)%MOD;
			//printf("%lld ",dp[cur][i]);
			va=va*dp2[i+2][n-i]%MOD;
			ans+=va;
			ans%=MOD;
		}
		//printf("\n");
		//printf("%lld %lld\n",pw,ans);
	}
	for(int i=1;i<=now;i++){
		group[v]=i;
		dfs(v+1,i);
	}
}

