        if(X[i] > 1) {
            tb = max(tb + 1, tr);
            while(tb <= K && ch[tb] != 'b' && used[tb]) tb++;
            used[tb] = 1;
            TR[i] = tr;
            TB[i] = tb;
        } else {
            TR[i] = tr;
            TB[i] = tr;
        }
        if(tr > K || tb > K) return;
    }
    for(int i = 1; i <= sz; i++) {
        int tt = TB[i] + 1;
        for(int j = 1; j <= X[i] - 2; j++) {
            while(tt <= K && used[tt]) tt++;
            if(tt > K) return;
        }
    }
    int t = 0, rw = 0, bw = 0;
    for(int i = 1; i <= sz; i++)
        if(X[i] == 1) {
            rw++;
            t++;
        } else {
            bw += X[i] - 1;
            rw += X[i];
            t += X[i] * 2 - 3;
        }
    t = n + 1 - t - sz;
    if(t < 0) return;
    ll tmp = Fac[sz];
    X[sz + 1] = 0; //!!!
    for(int i = 2, lst = 1; i <= sz + 1; i++)
        if(X[i] != X[lst]) {
            tmp = tmp * Rev[i - lst] % P;
            lst = i;
        }
    for(int r = 0; r <= t; r++)
        for(int b = 0; r + b <= t; b++) {
            add(ans, (!rw ? !r : C[r + rw - 1][r]) * (!bw ? !b : C[b + bw - 1][b]) % P * C[sz + (t - r - b)][sz] % P * tmp);
        }
}

void Search(int x, int lst, int tbr, int tot = 0) {
    calc(x - 1);
    for(int i = 1; i <= lst && i + tbr <= K && max(2ll, i * 2 - 2) + tot <= n + 3; i++) {
        X[x] = i;
        Search(x + 1, i, tbr + i, tot + max(2ll, i * 2 - 2));
    }
}

