inline LL qpow(LL a,LL b)
{
	LL ret=1;
	for(;b;b>>=1,a=a*a%mod)
		if(b&1)ret=ret*a%mod;
	return ret;
}
inline bool check(int tot)
{
	int cnt=0,cntr=tot,cntb=tot;
	For(i,1,K)
	if(str[i]=='r')
	{
		if(cntr)cntr--;
		else cnt-=cnt>0;
	}
	else
	{
		if(cntb>cntr&&cntb&&st[cntb])cnt+=st[cntb--]-1;
		else cnt-=cnt>0;
	}
	return !cnt&&!cntr&&(!cntb||st[cntb]==0);
}
LL b[N],r[N],w[N],f[N],g[N];
inline LL CC(int a,int b){return !b?!a:C[a+b-1][b-1];}
inline LL Solve(int tot,int res)
{
	int bcnt=0,rcnt=0,wcnt=0;
	For(i,1,tot)bcnt+=st[i],rcnt+=st[i]+1;wcnt=tot+1;
	For(i,0,res)f[i]=g[i]=0;
	For(i,0,res)
		For(j,0,res-i)
			Add(f[i+j],CC(i,rcnt)*CC(j,bcnt)%mod);
	For(i,0,res)
		For(j,0,res-i)
			Add(g[i+j],f[i]*CC(j,wcnt)%mod);
	return g[res];
}
inline void dfs(int cnt,int res,int now,LL val)
{
	if(res-max(now*2-1,1)-1<0)
	{
		if(check(cnt))Add(ans,Solve(cnt,res)*fac[cnt]%mod*val%mod);
