#include <iostream>
#include <algorithm>
#include <vector>
using namespace std;

long long mod = 1000000007;
long long N, K;
long long nr[109][109];
long long dp[109][109], nex[109][109];

string S;
int LIM[109];
bool used[109];

void init() {
	for (int i = 0; i <= 100; i++) {
		for (int j = 0; j <= 100; j++) {
			if (i == 0 || j == 0) nr[i][j] = 1;
			else nr[i][j] = (nr[i - 1][j] + nr[i][j - 1]) % mod;
		}
	}
}

long long ncr(int n, int r) {
	if (r < 0 || n < r) return 0;
	return nr[n - r][r];
}

long long solve(int cnt1, int cnt2) {
	//  1: 
	for (int i = 0; i < 100; i++) { used[i] = false; LIM[i] = 0; }

	//  2: 
	int f1 = 0, f2 = 0; vector<int> E;
	for (int i = 0; i < K; i++) {
		if (S[i] == 'r' && f1 < cnt1 + cnt2) { f1++; used[i] = true; }
		if (S[i] == 'b' && f2 < min(f1, cnt1)) { f2++; used[i] = true; E.push_back(i); }
	}
	if (f1 < cnt1 + cnt2 || f2 < cnt1) return 0;
	for (int i = 0; i < cnt1; i++) {
		for (int j = E[i] + 1; j < K; j++) {
			if (used[j] == false) LIM[i]++;
		}
	}

	//  3: 
	for (int i = 0; i <= 70; i++) {
		for (int j = 0; j <= 70; j++) { dp[i][j] = 0; nex[i][j] = 0; }
	}

	//  4: DP 
	int V = N - 2 * (cnt1 + cnt2) + 1;
	int MaxLetter = (V / 2);

	dp[0][0] = 1;
	for (int i = 0; i <= MaxLetter; i++) {
		for (int j = 0; j <= cnt1; j++) {
			for (int k = 0; k <= MaxLetter; k++) {
				if (dp[j][k] == 0) continue;

				int cx = j, cy = k, cnt = 0;
				while (cx <= cnt1 && cy <= min(LIM[cnt1 - cx], MaxLetter)) {
					nex[cx][cy] += ncr(cx, j) * dp[j][k];
					nex[cx][cy] %= mod;
					cx += 1; cy += i;
				}
			}
		}
		for (int j = 0; j <= cnt1; j++) {
			for (int k = 0; k <= MaxLetter; k++) {
				dp[j][k] = nex[j][k];
				nex[j][k] = 0;
			}
		}
	}

	//  5: 
	long long ret = 0;
	for (int i = 0; i <= MaxLetter; i++) {
		int minimal_letter = 2 * (cnt1 + cnt2) - 1 + i * 2;
		int ranged = (cnt1 + cnt2 + 1) + (cnt1 * 3 + i * 2) + cnt2;
		long long V1 = dp[cnt1][i];
		long long V2 = ncr(ranged + (N - minimal_letter) - 1, N - minimal_letter);
		ret += (V1 * V2);
		ret %= mod;
	}

	long long ret2 = ncr(cnt1 + cnt2, cnt2);
	return (ret * ret2) % mod;
}

int main() {
	cin >> N >> K >> S;
	init();

	long long Answer = 0;
	for (int i = 0; i <= N; i++) {
		for (int j = 0; j <= N; j++) {
			long long rem = solve(i, j);
			//cout << i << " " << j << " " << rem << endl;
			Answer += rem;
			Answer %= mod;
		}
	}

	cout << Answer << endl;
	return 0;
}

