void gen(int lst) {
    // if (lst == 3) ps("??");
    v.pb(cur);
    for (int i = lst; ; ++i) {
        int c = clen; if (c) c ++;
        int len = max(1,2*i-3);
        if (c+len > N) return;
        clen = c+len; cur.pb(i); gen(i);
        clen = c; if (clen) clen --; cur.pop_back();
    } 
}

bool ok(vi needR) {
    deque<int> needB; int need = 0;
    trav(c,s) {
        if (c == 'r') {
            if (sz(needR)) {
                int x = needR.back()-1; needR.pop_back();
                if (x) needB.pb(x);
            } else if (need) need --; 
        } else {
            if (sz(needB)) {
                int x = needB.front()-1; needB.pop_front();
                need += x;
            } else if (need) need --;
        }
    }
    // ps("")
    return !sz(needR) && !sz(needB) && !need;
}

int main() {
