    gen(1);
    mi ret(0);
    trav(t,v) {
        if (!sz(t)) ret += 1;
        else {
            int a = sz(t)-1, b = 0, c = sz(t)+1;
            map<int,int> m;
            mi zz(0);
            // a: # at least, b: possible 1+x, c: (1+x+x^2+...)
            trav(x,t) {
                if (x == 1) a++, c++;
                else a += 2*x-3, b += 2, c += 2*x-3;
            }
            F0R(i,b+1) {
                // a+i numbers, (1+x+x^2+...)^{c+i}
                zz += comb[b][i]*comb[N-(a+i)+(c+i-1)][c+i-1];
                // ret += comb[b][i]*comb[N-(a+i)+sz(t)][sz(t)];
            }
            FOR(i,1,sz(t)+1) zz *= i;
            trav(x,t)  {
                m[x] ++;
                zz /= m[x];
            }
            if (ok(t)) ret += zz;
        }
    }
    ps(ret);
}

