ll power(ll a,ll b=mo-2){
	ll ans=1;
	while (b){
		if (b&1)ans=ans*a%mo;
		a=a*a%mo;
		b>>=1;
	}return ans;
}
ll C(ll n,ll m){
	return ny[m]*ny[n-m]%mo*jc[n]%mo;
}
int check(int n){
	if (n==0){
		Ans++;
		return 1;
	}
	memset(mrk,0,sizeof mrk);
	memset(f,0,sizeof f);
	memset(sf,0,sizeof sf);
	if (!st[n])return 0;
	for (int i=1;i<=n;i++){
		mrk[st[i]]=1;
		for (int j=max(st[i],f[i-1]);s[j];j++)
			if (s[j]=='b'&&!mrk[j]){
				f[i]=j;
				mrk[j]=1;
				break;
			}
		if (a[i]==1)mrk[f[i]]=0,f[i]=0;
		if (!st[i]||!f[i]&&a[i]>1)return 0;
		if (a[i]==1)break;
	}
	for (int i=k;i>=1;i--)
		sf[i]=sf[i+1]+(!mrk[i]);
	for (int i=1;i<=n;i++)
		if (sf[f[i]]+2<a[i])return 0;

