	for (int i=1;i<=_N;i++)
		sum+=cnt[i],ans=ans*ny[cnt[i]]%mo;
	ans=ans*jc[sum]%mo;
	for (int i=1;i<=n;i++)
		n1+=2*a[i],m1+=2*(a[i]>=2);
	ans=ans*C(_N+m1-1,n1-1);
	Ans=(Ans+ans)%mo;
