		ans.x = x + b.x;
		if (ans.x >= MOD)
			ans.x -= MOD;
		return ans;
	}
	ModInt &operator+=(ModInt b) {
		x += b.x;
		if (x >= MOD)
			x -= MOD;
		return *this;
	}
	ModInt operator-(ModInt b) {
		ModInt ans;
		ans.x = x - b.x;
		if (ans.x < 0)
			ans.x += MOD;
		return ans;
	}
	ModInt &operator-=(ModInt b) {
		x -= b.x;
		if (x < 0)
			x += MOD;
		return *this;
	}
	ModInt operator-() {
		ModInt ans;
		if (x == 0)
			return ans;
		ans.x = MOD - x;
		return ans;
	}
	ModInt operator*(ModInt b) {
		return ModInt(x * b.x);
	}
	ModInt &operator*=(ModInt b) {
		x = (x * b.x) % MOD;
		return *this;
	}
	ModInt inv() {
		if (MOD >= 2) {
			if (x >= REV)
				return ModInt(pw(x, MOD - 2));
			else {
				if (flag[x])
					return rev[x];
				else {
					flag[x] = 1;
					return rev[x] = pw(x, MOD - 2);
				}
			}
		}
		return ModInt();
	}
	ModInt operator/(ModInt b) {
		return ModInt(x * b.inv().x);
	}
	ModInt& operator/=(ModInt b) {
		x = (x * b.inv().x) % MOD;
		return *this;
	}
	bool operator==(ModInt b) {
		return x == b.x;
	}
	bool operator!=(ModInt b) {
		return x != b.x;
	}
};

template<ll MOD, int REV>
bool ModInt<MOD, REV>::flag[] = {};

template<ll MOD, int REV>
ll ModInt<MOD, REV>::rev[] = {};

