struct st {
	int pos;
	int fr;
	int nd;
	deque<int> dq;
	st() {
		pos = fr = nd = 0;
	}
	int add(int sz) {
		while (pos < s.size() && s[pos] == 'b') {
			if (nd) {
				--nd;
				fr += dq.front();
				dq.pop_front();
			}
			else if (fr)
				--fr;
			++pos;
		}
		if (pos == s.size())
			return 0;
		pos += 1;
		if (sz) {
			++nd;
			dq.push_back(sz - 1);
		}
		return 1;
	}
	int check() {
		int pos2 = pos;
		int fr2 = fr;
		int nd2 = nd;
		auto dq2 = dq;
		while (pos2 < s.size()) {
			if (s[pos2] == 'r') {
				if (fr2)
					--fr2;
			}
			else {
				if (nd2) {
					--nd2;
					fr2 += dq2.front();
					dq2.pop_front();
				}
				else if (fr2)
					--fr2;
			}
			++pos2;
		}
		if (nd2 || fr2)
			return 0;
		return 1;
	}
};

int gn;

double sum = 0;


int Cnt;
vector<int> stck;

void run(int n, int cnt, int lst, int cntlst, st cur, array<num, MX> a) {
	if (cur.check()) {
		for (int i = 0; i <= gn; ++i)
			ans += a[i];
	}
	else
		return;

	if (n >= 2) {
		for (int i = min(lst, n / 2); i >= 0; --i) {
			auto cur2 = cur;
			if (!cur2.add(i))
				continue;
			if (lst == i)
				++cntlst;
			else
				cntlst = 1;
			array<num, MX> b = {};
			//auto start = std::chrono::high_resolution_clock::now();
			for (int j = 0; j <= gn; ++j) {
				if (a[j].x == 0)
					continue;
				for (int k = 2 * i; j + k <= gn; ++k)
					b[j + k] += a[j] * dp[i][k];
			}
			for (int j = 0; j <= gn; ++j) {
				b[j] *= (cnt + 1);
				if (cntlst != 1)
					b[j] /= cntlst;
			}
			//auto end = std::chrono::high_resolution_clock::now();
			//sum += std::chrono::duration<ld>(end - start).count();
			stck.push_back(i);
			if (i != 0)
				run(n - 2 * i, cnt + 1, i, cntlst, cur2, b);
			else
				run(n - 2, cnt + 1, i, cntlst, cur2, b);
			stck.pop_back();
		}
	}
}


num fc[MX * 2];
num bfc[MX * 2];

num cnk(int n, int k) {
	if (k > n)
		return 0;
	return fc[n] * bfc[k] * bfc[n - k];
}

