long long solve () {
	for (int i = 0; i < m; i++) vis[i] = true;
	for (int i = 0; i < top; i++) {
		int lst = m;
		for (int j = m - 1, k = 0; j >= 0; j--) {
			if (!vis[j]) continue;
			if (k < stk[i] - 1) vis[j] = false, k++;
		}
		for (int j = m - 1; j >= 0; j--) {
			if (!vis[j]) continue;
			if (stk[i] && str[j] == 'b') {
				lst = j;
				break;
			}
			if (!stk[i] && str[j] == 'r') {
				lst = j;
				break;
			}
		}
		if (lst >= m) return 0ll;
		vis[lst] = false;
		if (stk[i]) {
			bool flag = false;
			for (int j = lst; j >= 0; j--) {
				if (!vis[j]) continue;
				if (str[j] == 'r') {
					flag = true, vis[j] = false;
					break;
				}
			}
			if (!flag) return 0ll;
		}
	}
	long long res = 1ll;
	for (int i = 0; i <= n; i++) tmp[i] = 0;
	for (int i = 0; i < top; i++) tmp[stk[i]]++;
	for (int i = 0, j = 0; i <= n; i++) {
		j += tmp[i];
		res = res * c[j][tmp[i]] % mod;
	}

	int num1 = 0, num2 = 0;
	for (int i = 0; i < top; i++) {
		if (stk[i]) num1 += 2 * stk[i] - 1, num2 += 2;
		else num1++;
	}
	if (top) num1 += top - 1, num2 += 2;
	else num1++;
	res = res * c[n + num2 - 1][num1 + num2 - 1] % mod;
	return res;
}

void dfs (int mn, int total) {
	if (mn == n + 1) ans = (ans + solve()) % mod;
	if (mn <= n) dfs(mn + 1, total);
	int t = mn ? mn << 1 : 2;
	if (total + t <= n + 1) {
		stk[top++] = mn;
		dfs(mn, total + t);
		top--;
	}
}

