void dod(vector<int> pom,int zak){
  R(i,SZ(pom))if(i != zak && (i == 0 || pom[i-1])){
    if(i)pom[i-1]--;
    pom[i]++;
    add(pom);
    pom[i]--;
    if(i)pom[i-1]++;
  }
  if(SZ(pom) != zak){
    if(SZ(pom))pom.back()--;
    pom.PB(1);
    add(pom);
  }
}
int pot(int a,int w){
  int r = 1;
  while(w){
    if(w & 1){
      r = r * a % P;
    }
    w/=2;
    a = a * a % P;
  }
  return r;
}
