		int tlen=len;
		if(m>1)tlen++;
		if(a[m]==1)tlen++;
		else tlen+=(a[m]-1)*2-1;
		if(!dfs(m+1,a[m],sum+a[m],tlen))
			break;
	}
	return true;
}
