#define MAX_N 107

int N, K, tot, res;
int mch[MAX_N], pos[MAX_N], sum[MAX_N];
int fac[MAX_N * 3], ifac[MAX_N * 3];
char s[MAX_N];
bool vis[MAX_N];

inline int C(int x, int y) {
    if (y < 0 || x < y) return 0;
    return mul(fac[x], mul(ifac[y], ifac[x - y]));
}

void init() {
    int MX = 300;
    fac[0] = ifac[0] = 1;
    Rep(i, MX) fac[i] = mul(fac[i - 1], i);
    ifac[MX] = qpow(fac[MX], MOD - 2);
    irep(i, MX, 2) ifac[i - 1] = mul(ifac[i], i);
    Rep(i, K) if (s[i] == 'r') {
        pos[++tot] = i;
        rep(j, i + 1, K) if (s[j] == 'b' && !vis[j]) {
            mch[i] = j;
            vis[j] = true;
            break;
        }
    }
}

int tp[MAX_N];
bool check(int cur) {
    if (cur > tot) return false;
    Rep(i, N) sum[i] = 0;
    int p = 1;
    IRep(i, cur) {
        sum[pos[i]]++;
        if (tp[p] > 1) {
            if (!mch[pos[i]]) return false;
            sum[mch[pos[i]]] += tp[p] - 1;
        }
        p++;
    }
    IRep(i, K) {
        sum[i] += sum[i + 1];
        if (sum[i] > K - i + 1) return false;
    }
    return true;
}

bool dfs(int deg, int pre, int len) {
    if (!check(deg - 1)) return false;
    int t = deg;
    Lep(i, deg) t += 2 * tp[i] - 1;
    int ans = mul(C(N - len + t - 1, t - 1), fac[deg - 1]), tmp = 1;
    Lep(i, deg) {
        if (tp[i] != tp[i - 1]) {
            ans = mul(ans, ifac[tmp]);
            tmp = 0;
        }
        ++tmp;
    }
    ans = mul(ans, ifac[tmp]);
    // printf("%d:  ", ans);
    // Lep(i, deg) printf("%d ", tp[i]);
    // puts("");
    inc(res, ans);
    if (deg > tot) return true;
    rep(i, pre, 70) {
        int L = len;
        if (deg > 1) L++;
        if (i == 1)
            L++;
        else
            L += i - 2 + i - 1;
        if (L > N) break;
        tp[deg] = i;
        if (!dfs(deg + 1, i, L)) break;
    }
    return true;
}

void solve() {
    dfs(1, 1, 0);
    printf("%d\n", res);
}

int main() {
    read(N), read(K);
    scanf("%s", s + 1);
    init();
    solve();
    return 0;
}

