					continue;
				for (int k = 2 * i; j + k <= gn; ++k)
					b[j + k] += a[j] * dp[i][k];
			}
			for (int j = 0; j <= gn; ++j) {
				b[j] *= (cnt + 1);
				if (cntlst != 1)
					b[j] /= cntlst;
			}
