ll qpow(ll x,ll p){
	ll ret=1;
	for(;p;p>>=1,x=x*x%cys) if(p&1) ret=ret*x%cys;
	return ret;
}

ll getans(){
	memset(vis,0,sizeof(vis));
	int pl=0,num=0;
	for(int i=1;i<=cnt;i++) if(f[i]==1) num++;
	for(int i=1;i<=cnt;i++){
		pl=nxt[pl][0];
		if(pl>k) return 0;
		a[i]=pl,vis[pl]=1;
	}
	for(int i=1;i<=cnt-num;i++){
		int tmp=nxt[a[i]][1];
		while(vis[tmp]) tmp=nxt[tmp][1];
		if(tmp>k) return 0;
		b[i]=tmp,vis[tmp]=1;
	}
	int now=cnt-num,pcnt=f[now];
	while(f[now]==2) now--,pcnt=f[now];
	for(int i=k;i>=1;i--){
		if(vis[i]) continue;
		if(i<b[now]) return 0;
		pcnt--;
		if(pcnt==2) now--,pcnt=f[now];
		if(!now) break;
	}
	if(pcnt>2) return 0;
	int rnum=num,bnum=0,wnum=cnt-1,all=n;
	for(int i=1;i<=cnt-num;i++) rnum+=f[i]-2,bnum+=f[i]-1;
	all-=rnum+bnum+wnum;
	if(all<0) return 0;
	ll res=fac[cnt],lst=1;
	for(int i=1;i<=cnt;i++) if(f[i]!=f[i-1]&&i>1) res=res*inv[i-lst]%cys,lst=i;
	res=res*inv[cnt+1-lst]%cys;
	return res*C(all+rnum+bnum+wnum+(cnt-num)*2+1,rnum+bnum+wnum+(cnt-num)*2+1)%cys;
}

void dfs(int now,int sum,int all,bool tp){
	if(all>n) return;
	if(!tp) ans=mod(ans+getans());
	if(!sum||!now) return;
	for(int i=0;i<=sum/now;i++){
		dfs(min(now-1,sum-i*now),sum-i*now,all+i*(now==1?1:2*now-3),i==0?1:0);
		f[++cnt]=now;
	}
	cnt-=sum/now+1;
}

