			c[i][j] = (c[i - 1][j - 1] + c[i - 1][j]) % mod;
	}
	inv[0] = fac[0] = 1;
	for (int i = 1; i <= 300; i++)
		fac[i] = (fac[i - 1] * i) % mod, inv[i] = (quickpow(fac[i], mod - 2));
	dfs(70, 1, 0);
	printf("%lld\n", ans);

	return 0;
}

