    if(!check()) return false;
    int k = 1 + cnt;
    for(int i = 1 ; i <= cnt ; ++i) {
        if(L[i] == 1) ++k;
        else k += 2 * L[i] - 1;
    }
    int res = C[N - len + k - 1][k - 1];
    res = mul(res,fac[cnt]);
    int t = 0;
    for(int i = 1 ; i <= cnt ; ++i) {
        if(L[i] != L[i - 1]) {
            res = mul(res,invfac[t]);
            t = 0;
        }
        ++t;
    }
    res = mul(res,invfac[t]);
    update(ans,res);
    if(dep + 1 > tot) return true;
    for(int i = pre ; i <= 70 ; ++i) {
        int tl = len;
        if(dep != 0) ++tl;
        if(i == 1 || i == 2) tl += 1;
        else tl += i - 2 + i - 1;
        if(tl > N) break;
        L[dep + 1] = i;
        if(!dfs(i,dep + 1,tl)) break;
    }
    return true;
}
void Solve() {
    read(N);read(K);
    scanf("%s",s + 1);
    C[0][0] = 1;
    for(int i = 1 ; i <= 1000 ; ++i) {
        C[i][0] = 1;
        for(int j = 1 ; j <= i ; ++j) {
            C[i][j] = inc(C[i - 1][j - 1],C[i - 1][j]);
        }
    }
    fac[0] = 1;
    for(int i = 1 ; i <= 1000 ; ++i) fac[i] = mul(fac[i - 1],i);
    invfac[1000] = fpow(fac[1000],MOD - 2);
    for(int i = 999 ; i >= 0 ; --i) invfac[i] = mul(invfac[i + 1],i + 1);
    tot = 0;
    for(int i = 1 ; i <= K ; ++i) {
        if(s[i] == 'r') pos[++tot] = i;
        sum[i][0] = sum[i - 1][0];
        sum[i][1] = sum[i - 1][1];
        if(s[i] == 'r') sum[i][0]++;
        else sum[i][1]++;
    }
    dfs(1,0,0);
    out(ans);enter;
}
