	}
	for(int i=1;i<=ntop;++i){
		beg[i]=max(beg[i],beg[i-1]+1);
		rep(_,1,st[i]-2){
			while(beg[i]<=m&&vis[beg[i]])++beg[i];
			if(beg[i]>m)return 0;
			++beg[i];
		}
	}
	return 1;
}
