ll quick_pow(ll x,ll a)
{
	ll ans=1;
	while(a)
	{
		if(a&1)ans=ans*x%MOD;
		x=x*x%MOD;
		a>>=1;
	}
	return ans;
}
bool check()
{
	memset(flag,0,sizeof(flag));
	while(!q.empty())q.pop();
	int tot=0;
	for(int i=m;i;i--)
	{
		for(int j=1;j<=a[i];j++)
		  tmp[++tot]=i-1;
	}
	int now=1;
	for(int i=1;i<=k;i++)
	{
		if(s[i]=='r')
		{
			q.push(i);
		}
		if(s[i]=='b')
		{
			if(now<=tot&&!q.empty())
			{
				st[now]=i;
				flag[q.front()]=flag[i]=1;
				q.pop();
				now++;
			}
		}
	}
	if(now<=tot)return 0;
	for(int i=1;i<=a[0];i++)
	{
		if(q.empty())return 0;
		flag[q.front()]=1;
		q.pop();
	}
	now=1;
	for(int i=1;i<=tot;i++)
	{
		while(now<=st[i])now++;
		for(int j=1;j<=tmp[i];j++)
		{
			while(flag[now])now++;
			if(now>k)return 0;
			flag[now]=1;
		}
	}
	return 1;
}
ll solve()
{
	if(!check())return 0;
	int l=n,tot=0;
	for(int i=0;i<=m;i++)
	{
		l-=a[i]*(len[i]+1);
		tot+=a[i];
		tot+=a[i]*(2*i+1);
	}
	l++;
	ll ans=fact[l+tot]*inv[l]%MOD*inv[tot]%MOD;
	tot=0;
	for(int i=0;i<=m;i++)
	{
		tot+=a[i];
