void check(int len, int num) {
	memset(vis, 0, sizeof(vis));
	int p1 = 1, p2 = 1, p3 = 1;
	for (int i = 1; i <= k; ++i) {
		if (s[i] == 'r') {
			if (p1 <= len) vis[p1++] = 1;
			else {
				while (p3 <= len && vis[p3] == a[p3]) ++p3;
				vis[p3]++;
			}
		}
		else if (s[i] == 'b') {
			while (p2 <= len && (vis[p2] >= 2 || a[p2] == 1)) ++p2;
			if (p2 <= len && vis[p2] == 1) vis[p2++] = 2;
			else {
				while (p3 <= len && vis[p3] == a[p3]) ++p3;
				vis[p3]++;
			}
		}
	}
	for (int i = 1; i <= len; ++i)
		if (vis[i] != a[i]) return;
	long long mul = fac[len], cnt = len + 1;
	for (int i = 1, j; i <= len; i = j + 1) {
		j = i;
		while (a[j] == a[j + 1] && j < len) ++j;
		mul = mul * fav[j - i + 1] % mod;
		cnt += (j - i + 1) * (a[i] * 2 - 1);
	}
	mul = mul * c[n - num + cnt - 1][cnt - 1] % mod;
	ans += mul;
	if (ans >= mod) ans -= mod;
}

void dfs(int x, int len, int num) {
	if (num > n) return;
	check(len, num);
	for (int i = 1; i <= x; ++i) {
		a[len + 1] = i;
		dfs(i, len + 1, num + (i > 1 ? i * 2 - 3 : 1) + (len > 0));
	}
}

