			c[i][j] = (c[i - 1][j] + c[i - 1][j - 1]) % mod;
	}
	fac[0] = fav[0] = inv[0] = 1;
	fac[1] = fav[1] = inv[1] = 1;
	for (int i = 2; i < N; ++i) {
		inv[i] = - mod / i * inv[mod % i] % mod + mod;
		fac[i] = fac[i - 1] * i % mod;
		fav[i] = fav[i - 1] * inv[i] % mod;
	}
	dfs(k, 0, 0);
	printf("%d\n", ans);
	return 0;
}

