void binomialCoefficients ()
{
    c[0][0] = 1;
    for (int i=1; i<=100; i++)
    {
        c[i][0] = 1;
        for (int j=1; j<=i; j++)
            c[i][j] = add (c[i - 1][j - 1], c[i - 1][j]);
    }
    fac[0] = 1;
    for (int i=1; i<=100; i++)
        fac[i] = mul (fac[i - 1], i);
    for (int i=0; i<=100; i++)
        inv[i] = power (fac[i], mod - 2);
}

void computeCntRB ()
{
    for (int l=1; l<=N; l++)
    {
        cntRB[l][0] = 1;
        for (int i=1; 2 * i - 1 <= l; i++)
            cntRB[l][i] = c[l + 1][2 * i];
    }
    for (int l=1; l<=N; l++)
        for (int j=0; j<=N; j++)
            for (int k=1; k<l; k++)
                adto (cntWRB[l][j], cntRB[l - k][j]);
}

