void dfs (int node)
{
    if (node == 1)
    {
        for (auto pp : sons[node])
        {
            int l = pp.second, id = pp.first;
            for (int i=1; i<=N; i++)
                currDp[i] = add (cntWRB[i][l], cntRB[i][l]);///can even have no white
            dfs (id);
        }
        return ;
    }
    int oldDp[109];
    memcpy (oldDp, currDp, sizeof (currDp));
    for (auto pp : sons[node])
        {
            int l = pp.second, id = pp.first;
            memset (currDp, 0, sizeof (currDp));
            for (int i=1; i<=N; i++)
                if (oldDp[i] != 0)
                    for (int j=2 * l; i + j<=N; j++)
                        adto (currDp[i + j], mul (oldDp[i], cntWRB[j][l]));///must have a white to separate
            dfs (id);
            memcpy (currDp, oldDp, sizeof (oldDp));
        }
    ways[node] = 0;
    for (int i=1; i<=N; i++)
        adto (ways[node], currDp[i]);
}

