#define P 1000000007
#define int long long

using namespace std;

typedef long long ll;

char ch[77];

void add(int &x, int y) {
    x = (x + y) % P;
}

int n, K, G[77][77], H[77][77], S[77], Fac[333], Rev[333];

ll powmod(ll x, ll y = P - 2, ll p = P) {
    ll ans = 1;
    while(y) {
        if(y & 1) ans = ans * x % p;
        x = x * x % p;
        y >>= 1;
    }
    return ans;
}

ll C(ll x, ll y) {
    if(!y) return 1;
    if(x < 0 || y < 0 || x < y) return 0;
    return Fac[x] * Rev[y] % P * Rev[x - y] % P;
}

ll calc(ll x, ll n) {
    ll ans = 0, nw = 1;
    for(int i = 1; i <= n; i++) {
        nw = nw * (x - i + 1) % P;
        add(ans, nw * Rev[i] % P * H[n][i]);
    }
    return ans;
}

int X[77], ans = 0;

void calc(int sz) {
    int t = 0, rw = 0, bw = 0;
    for(int i = 1; i <= sz; i++)
        if(X[i] == 1) {
            rw++;
            t++;
        } else {
            bw += X[i] - 1;
            rw += X[i];
            t += X[i] * 2 - 3;
        }
    t = n + 1 - t - sz;
    if(t < 0) return;
    int tmp = Fac[sz];
    X[sz + 1] = 0; //!!!
    for(int i = 2, lst = 1; i <= sz + 1; i++)
        if(X[i] != X[lst]) {
            tmp = tmp * Rev[i - lst] % P;
            lst = i;
        }
    for(int r = 0; r <= t; r++)
        for(int b = 0; r + b <= t; b++) {
            add(ans, C(r + rw - 1, r) * C(b + bw - 1, b) % P * C(sz + (t - r - b), sz) % P * tmp);
        }
}

void Search(int x, int lst, int tbr, int fir) {
    if(tbr > K) return;
    calc(x - 1);
    while(fir <= K && ch[fir] != 'r') fir++;
    if(fir > K) return;
    X[x] = 1;
    Search(x + 1, 1, tbr + 1, fir + 1);
    while(fir <= K && ch[fir] != 'b') fir++;
    if(fir <= K) for(int i = 2; i <= lst && i + tbr <= K; i++) {
        X[x] = i;
        Search(x + 1, i, tbr + i, fir);
    }
}

