inline void pre()
{
	scanf("%d%d",&n,&m);
	scanf("%s",b+1);
	for (i=0;i<=250;i++)
		for (j=0;j<=i;j++)
			if ((j==0) || (j==i))
				c[i][j]=1;
			else
				c[i][j]=(c[i-1][j]+c[i-1][j-1])%p;
	return;
}

inline void calc()
{
	for (i=1;i<=n;i++)
		h[i]=g[i];
	t=0;
	for (i=1,k=1,l=1;i<=n;i++)
		if ((b[i]=='r') && (h[k]>0))
			h[k]--,k++;
		else
		if ((b[i]=='b') && (h[l]>0) && (l<k))
			h[l]--,t=t+h[l],l++;
		else
		if (t>0)
			t--;
	if ((h[k]) || (h[l]) || (t))
		return;
	r=1;
	for (i=1;g[i];i=j)
	{
		for (j=i;g[i]==g[j];j++);
		r=1LL*r*c[j-1][i-1]%p;
		if (g[i]==1)
			t=t+j-i;
		else
			t=t+(j-i)*(2*g[i]-3);
	}
	t=t+k-2;
	if (n-t<0)
		return;
	s=(s+1LL*r*c[n+2*l-1][t+2*l-1])%p;
	return;
}

inline void dfs(int w,int x,int y,int z)
{
	if (w<0)
		return;
	if (w<=n)
		calc();
	if (x)
	{
		g[z+1]=1,dfs(w-2,x-1,1,z+1);
		for (int i=2;(i<=x) && (i<=y);i++)
			g[z+1]=i,dfs(w-2*i+2,x-i,i,z+1);
		g[z+1]=0;
	}
	return;
}

