int dp2[2][72][72][72];
int wh_ptr[72], gain[72];

signed main() {
  ios::sync_with_stdio(false); cin.tie(0);
  nCr[0][0] = 1;
  for (int i=1; i<=200; i++) {
    nCr[i][0] = 1;
    for (int j=1; j<=200; j++) nCr[i][j] = (nCr[i-1][j-1]+nCr[i-1][j])%MOD;
  }

  cin >> N >> K >> S;
  rep(i, K) {
    if (S[i] == 'r') black.pb(i);
    else white.pb(i);
  }
  if (black.empty()) {
    cout << 1 << "\n";
    return 0;
  }
  rep(i, max(N, K)+1) gain[i] = -INF, wh_ptr[i] = white.size();
  int head = 0;
  rep(i, black.size()) {
    int g = 0;
    while (head < white.size() && white[head] < black[i]) {
      head++, g++;
    }
    if (head == white.size()) {
      break;
    }
    head++;
    wh_ptr[i] = head;
    gain[i] = g;
  }

  dp1[0][0][0] = 1;
  for (int len=0; len<N; len++) {
    rep(num, N+1) {
      rep(last, 2) {
        add(dp1[len+1][num][0], dp1[len][num][last]); // 
        add(dp1[len+1][num+(last==0)][1], dp1[len][num][last]); // 
      }
    }
  }
  rep(len, N+1) rep(num, N+1) if (num>0) T[len][num-1] = (dp1[len][num][0]+dp1[len][num][1])%MOD;

  dp2[0][0][0][0] = 1;
  for (int star=N; star>=0; star--) {
    for (int len=N; len>=1; len--) {
      if (T[len][star] == 0) continue;

      rep(i, N+1) rep(j, N+1) rep(k, N+1) dp2[1][i][j][k] = dp2[0][i][j][k];
      rep(groups, N+1) rep(sum, N+1) rep(now, N+1) {
        if (dp2[0][groups][sum][now] == 0) continue;
        int cur = now;
        int val = (1LL*dp2[0][groups][sum][now]*T[len][star])%MOD;
        for (int num=1; groups+num<=min(N, (int)black.size()) && sum+num*len<=N; num++) {
          cur = max(cur-gain[groups+num-1], 0) + star;
          if (cur > N) continue;
          add(dp2[1][groups+num][sum+num*len][cur], (1LL*val*nCr[groups+1-1+num][num])%MOD);
          val = (1LL*val*T[len][star])%MOD;
        }
      }
      swap(dp2[0], dp2[1]);
    }
  }
  rep(i, N+1) rep(j, N+1) rep(k, N+1) dp2[1][i][j][k] = 0;
  rep(groups, N+1) rep(sum, N+1) rep(now, N+1) {
    int gain = 0;
    if (groups > 0) gain = white.size() - wh_ptr[groups-1];
    int cur = max(now-gain, 0);
    add(dp2[1][groups][sum][cur], dp2[0][groups][sum][now]);
  }
  swap(dp2[0], dp2[1]);

  for (int len=N; len>=1; len--) {
    rep(i, N+1) rep(j, N+1) rep(k, N+1) dp2[1][i][j][k] = dp2[0][i][j][k];
    rep(groups, N+1) rep(sum, N+1) rep(now, N+1) {
      if (dp2[0][groups][sum][now] == 0) continue;
      int cur = now;
      int val = (1LL*dp2[0][groups][sum][now])%MOD;
      for (int num=1; groups+num<=min(N, (int)black.size()) && sum+num*len<=N; num++) {
        add(dp2[1][groups+num][sum+num*len][cur], (1LL*val*nCr[groups+1-1+num][num])%MOD);
      }
    }
    swap(dp2[0], dp2[1]);
  }

  int s = 1; // all white
  for (int groups=1; groups<=N; groups++) rep(sum, N+1) rep(cur, N+1) {
    if (dp2[0][groups][sum][cur] == 0) continue;

    if (groups > black.size()) continue;
    int gain = black.size() - groups;
    if (gain < cur) continue;

    int yo = N-sum;
    if (yo < groups-1) continue;
    yo -= groups-1;
    add(s, (1LL*nCr[yo+groups][yo]*dp2[0][groups][sum][cur])%MOD);
  }
  cout << s << "\n";
  return 0;
}

