void update()
{
	if( N < sum ) return;
	int cnt = 0, red = 0, blue = 0;
	for(char c : S){
		if( c == 'r' ){
			if( red < sz) red++;
			else cnt -= cnt>0;
		}
		else{
			if( blue < red && A[blue] >= 2 ) cnt += A[blue++]-2;
			else cnt -= cnt>0;
		}
	}
	if(cnt || red != sz || A[blue] >= 2) return;

	ll m = F[sz];
	for(int i = 0; i < sz; i++) pr[A[i]] += 1;
	for(int i = 0; i <= A[0]; i++){
		if(pr[i]) m = m * FI[pr[i]] % MM;
		pr[i] = 0;
	}

	ans = (ans + C[t+N-1][N-sum] * m) % MM;
}

void dfs(int K, int mx){
	if( K == 0 ) return;
	for(int i = mx; i >= 1; i--){
		A[sz++] = i;
		t += i >= 2 ? 2 : 0;
		sum += i == 1 ? 2 : i*2-2;

		update();
		dfs(K-i, min(i, K-i));

		A[--sz] = 0;
		t -= i >= 2 ? 2 : 0;
		sum -= i == 1 ? 2 : i*2-2;
	}
}

