	if (y < 0) { b = b.inv(); y = -y; }
	for (; y; y>>=1) {
	    if (y&1) r *= b;
	    b *= b;
	}
	return r;
    }
    friend ModInt operator+(ModInt x, const ModInt y) { return x += y; }
    friend ModInt operator-(ModInt x, const ModInt y) { return x -= y; }
    friend ModInt operator*(ModInt x, const ModInt y) { return x *= y; }
    friend ModInt operator/(ModInt x, const ModInt y) { return x *= y.inv(); }
    friend bool operator<(const ModInt x, const ModInt y) { return x.x < y.x; }
    friend bool operator==(const ModInt x, const ModInt y) { return x.x == y.x; }
    friend bool operator!=(const ModInt x, const ModInt y) { return x.x != y.x; }
};

const LL MOD = 1000000007;
//const LL MOD = 998244353;
typedef ModInt<MOD> Mint;

int N, K;
char S[111];

bool ok(int r, int b, int x) {
    int u = 0;
    REP (i, K) {
	if (S[i] == 'r') {
	    if (r) { r--; u++; }
	    else if (x) x--;
	} else if (S[i] == 'b') {
	    if (b && u) { b--; u--; }
	    else if (x) x--;
	}
    }
    return r == 0 && b == 0 && x == 0;
}

Mint buf[2][72][72][72][4];
Mint (*cur)[72][72][4] = buf[0];
Mint (*nxt)[72][72][4] = buf[1];

void MAIN() {
    scanf("%d%d%s", &N, &K, S);

    cur[0][0][0][0] = 1;

    REP (i, N) {
	memset(nxt, 0, sizeof buf[0]);
	REP (r, i+1) REP (b, i+1) REP (x, i+1) {
	    nxt[r][b][x][0] += cur[r][b][x][0];
	    nxt[r+1][b][x][1] += cur[r][b][x][0];
	    nxt[r+1][b+1][x][2] += cur[r][b][x][0];

	    nxt[r][b][x][0] += cur[r][b][x][1];
	    nxt[r][b][x][1] += cur[r][b][x][1];
	    nxt[r][b+1][x][2] += cur[r][b][x][1];

	    nxt[r][b][x][0] += cur[r][b][x][2];
	    nxt[r][b][x][2] += cur[r][b][x][2];
	    nxt[r][b][x][3] += cur[r][b][x][2];

	    nxt[r][b][x][0] += cur[r][b][x][3];
	    nxt[r][b][x+1][2] += cur[r][b][x][3];
	    nxt[r][b][x][3] += cur[r][b][x][3];
	}
	swap(cur, nxt);
    }

    Mint ans = 0;
    REP (r, N+1) REP (b, N+1) REP (x, N+1) REP (c, 4) {
	Mint v = cur[r][b][x][c];
	if (v.x == 0) continue;
	assert(r >= b);
	if (ok(r, b, x)) {
	    ans += v;
	}
    }
    printf("%d\n", ans.geti());
}

