const ll mod = 1e9 + 7;
ll powmod(ll a, ll e) {
  ll sum = 1;
  ll cur = a;
  while (e > 0) {
    if (e % 2) {
      sum = sum * cur % mod;
    }
    cur = cur * cur % mod;
    e /= 2;
  }
  return sum;
}


const int M = 200000;
ll fact[M], invfact[M];
void init(void) {
  fact[0] = 1;
  REP(i, 1, M) {
    fact[i] = fact[i - 1] * i % mod;
  }
  invfact[M - 1] = powmod(fact[M - 1], mod - 2);
  for (int i = M - 2; i >= 0; --i) {
    invfact[i] = invfact[i + 1] * (i + 1) % mod;
  }
}

ll comb(int x, int y) {
  if (x < 0) {
    return y == 0 || y == x ? 1 : 1;
  }
  if (y < 0 || y > x) {
    return 0;
  }
  long r = fact[x] * (invfact[x - y] * invfact[y] % mod) % mod;
  return r;
}

void add(ll &x, ll y) {
  x = (x + y) % mod;
}


int n, k;
