ll match(const VI &v) {
  int m = v.size();
  VI r(m, -1);
  VI used(k, 0);
  int pos = 0;
  REP(i, 0, m) {
    while (pos < k && s[pos] != 'r') {
      pos++;
    }
    if (pos == k) return 0;
    r[i] = pos;
    used[pos] = 1;
    pos++;
  }
  pos = 0;
  REP(i, 0, m) {
    if (v[i] <= 1) break;
    pos = max(pos, r[i] + 1);
    while (pos < k && s[pos] != 'b') { pos++; }
    if (pos == k) return 0;
    used[pos] = 1;
    pos++;
  }
  pos = 0;
  REP(i, 0, m) {
    if (v[i] <= 2) break;
    int res = v[i] - 2;
    REP(j, 0, res) {
      while (pos < k && used[pos] == 1) { pos++; }
      if (pos == k) return 0;
      used[pos] = 1;
      pos++;
    }
  }
  ll tmp = fact[v.size()];
  map<int, int> freq;
  REP(i, 0, v.size()) {
    freq[v[i]] += 1;
  }
  for (auto p: freq) {
    tmp = tmp * invfact[p.second] % mod;
  }
  int x = n + 1;
  int y = 0;
  REP(i, 0, v.size()) {
    if (v[i] >= 2) {
      x += 2;
    }
    y += 2 * v[i];
  }
  if(DEBUG){
    DEBUGP(x);
    DEBUGP(y);
  }
  tmp = tmp * comb(x, y) % mod;
  return tmp;
}

int cnt = 0;
