void output(const VI &vi) {
  cnt++;
  if (DEBUG && n <= 10) {
    cerr << "out:";
    for (auto c: vi) cerr << " " << c;
    cerr << endl;
  }
  ll res = match(vi);
  add(tot, res);
  if (DEBUG) {
    DEBUGP(res);
    cerr<<endl;
  }
}


void calc(int rem, int ma, int maxlen, VI &v) {
  if (rem < -1) return;
  output(v);
  if ((int) v.size() >= maxlen) return;
  for (int i = ma; i >= 1; --i) {
    v.push_back(i);
    int len = i <= 1 ? 1 : 2 * i - 3;
    calc(rem - len - 1, i, maxlen, v);
    v.pop_back();
  }
}

