int qpow(int base,int tms)
{
	int tmp = 1;
	while(tms)
	{
		if(tms & 1) tmp = tmp * 1ll * base % mod;
		base = base * 1ll * base % mod;
		tms >>= 1;
	}
	return tmp;
}

bool chk(int cn)
{
	static int sum[75]; memset(sum,0,sizeof(sum));
	for(int i = cn;i >= 1; -- i)
	{
		if(!pos[i]) return false;
		sum[pos[i]] ++;
		if(ln[cn - i + 1] >= 2)
		{
			if(!mat[pos[i]]) return false;
			sum[mat[pos[i]]] += ln[cn - i + 1] - 1;
		}
	}
	for(int i = k;i >= 1; -- i)
	{
		sum[i] += sum[i + 1];
		if(sum[i] > k - i + 1) return false;
	}
	return true;
}

bool dfs(int mi,int cnt,int mus)
{
	if(!chk(cnt)) return false;
	int k = cnt + 1;
