int F = 0;
int mark[maxN];
const int mod = (int)1e9 + 7;
int mult(int a, int b) {
    return (1LL * a * b) % mod;
}
int sum(int a, int b) {
    int s = a + b;
    if (s >= mod) s -= mod;
    return s;
}
int fact[2 * maxN];
int invfact[2 * maxN];
int inv[2 * maxN];
int cnk(int n, int k) {
    if (n < k || k < 0) return 0;
    return mult(fact[n], mult(invfact[n - k], invfact[k]));
}
bool check() {
    memset(mark, -1, sizeof mark);
    int ptr = 0;
    int f = k;
    while (ptr < sz && blue[ptr] == 0) {
        if (f == 0) return false;
        if (c[f] == 'b') {
            f--;
        }
        else {
            mark[f] = 1;
            f--;
            ptr++;
        }
    }
    ptr--;
    //[0 .. ptr] - ok
    //
    int ptrR = sz - 1;
    int ptrB = sz - 1;
    for (int i = 1; i <= k; i++) {
        if (mark[i] != -1) continue;
        if (c[i] == 'r' && ptrR > ptr) {
            ptrR--;
            mark[i] = 1;
        }
        if (c[i] == 'b' && ptrB > ptrR) {
            ptrB--;
            mark[i] = 1;
        }
    }
    if (ptrR != ptr || ptrB != ptr) return false;
    int where = sz - 1;
    int need = red[sz - 1];
    for (int i = 1; i <= k; i++) {
        if (need == 0) return true;
        if (mark[i] == 1) continue;
        if (where > ptr) {
            need--;
            if (need == 0) {
                where--;
                need = red[where];
            }
        }
    }
    return (need == 0 || where == ptr);
}
int his_cnt[maxN];
void calc() {
    /*for (int i = 0; i < sz; i++) {
        cout << red[i] << " " << blue[i] << endl;
    }
    cout << "----------------------" << endl;*/
    if (!check()) {
//        cout << ":(" << endl;
        return;
    }

    int val = fact[sz];
    memset(his_cnt, 0, sizeof his_cnt);
    int groups = 0;
    int other = 0;
    for (int i = 0; i < sz; i++) {
        his_cnt[blue[i]]++;
        val = mult(val, inv[his_cnt[blue[i]]]);
        groups += blue[i] + red[i];
        if (blue[i]) other++;
    }
    //so we have
    // x_1 + ... + x_groups + y_1 + .. + y_2*other + c_1 + c_2 + p_1 + ... + p_(sz - 1) = n
    // groups + 2 * other + sz - 1 + 2
    int all_gr = groups + 2 * other + sz - 1 + 2;
    ans = sum(ans, mult(val, cnk(n + all_gr - 1 - groups - (sz - 1), all_gr - 1)));
}
void gen(int pos, int total) {
    if (total + sz - 1 > n) return;
    if (sz > LIM) return;
    if (pos == r) {
        F++;
        calc();
        return;
    }
    gen(pos + 1, total);
    if (pos == 0) {
        red[sz] = 1;
        blue[sz] = 0;
        sz++;
        gen(pos, total + 1);
        sz--;
    }
    else {
        red[sz] = cnt1[pos];
        blue[sz] = cnt2[pos];
        sz++;
        gen(pos, total + red[sz - 1] + blue[sz - 1]);
        sz--;
    }
}
