void Calc()
{
	int ga = g, gb = g, tot = 0;
	for (int i = 1; i <= k; i++)
		if (s[i] == 'r' && ga)
			ga--;
		else
			if (s[i] == 'b' && gb > ga && S[gb] > 1)
				tot += S[gb] - 2, gb--;
			else
