	tot = 1;
	for (int i = 1, j = 0; i <= g; i = j + 1)
	{
		for (; j < g && S[j + 1] == S[i];)j++;
		tot = (ll)tot * c[j][j - i + 1] % mod;
	}
	ga = Ga - 1;
	gb = Gb + Ga;
	ans = ((ll)tot * c[n - ga + gb][gb] + ans) % mod;
}
