				int c = (LL)f[m][A][S] * jc[A + B] % mod * inv[B] % mod;
				int H1 = 2 * S + 2 * B, H2 = A * 2;
				int ga = H1 - 1, gb = H1 + H2;
				ans = (ans + (LL)c * C[n - ga + gb][gb]) % mod;
			}
		}
	} pr2(ans);
	return 0;
}

