const int N=255,mo=1e9+7;
int n,k,ans,g,Ga,Gb;
int c[N][N],st[N];
char s[N];
void calc(){
	int ga=g,gb=g,tot=0;
	fo(i,1,k)
		if(s[i]=='r'&&ga)--ga;else
		if(s[i]=='b'&&gb>ga&&st[gb]>1)tot+=st[gb]-2,--gb;else
		if(tot)--tot;
	for(;st[gb]==1;)--gb;
	if(ga||gb||tot)return;
	tot=1;
	for(int i=1,j=0;i<=g;i=j+1){
		for(;j<g&&st[j+1]==st[i];)++j;
		tot=(ll)tot*c[j][j-i+1]%mo;
	}
	ga=Ga-1;gb=Gb+Ga;
	ans=((ll)tot*c[n-ga+gb][gb]+ans)%mo;
}
