	calc();
	++g;
	int la=Ga,lb=Gb;
	for(int i=len;_n+(i==1?2:i*2-2)<=n+1&&_k+i<=k;++i)
		Ga=la+(i==1?2:i*2-2),Gb=lb+(i>1)*2,st[g]=i,find(_n+(i==1?2:i*2-2),_k+i,i);
	--g;
}
