const ll MOD = 1e9+7;

// n^m%MOD O( log(m) )
ll modpow(ll n, int m){
	ll ret=1;
	for(int i=1; i<=m; i<<=1, (n*=n)%=MOD){
		if( m&i ) (ret *= n) %= MOD;
	}
	return ret;
}

// 
ll modinv(ll n){
	return modpow( n, MOD-2 );
}

// O(1)  O( n*log(n) )
// fact[n]     : n
// fact.inv[n] : n
class FACTORIAL{
public:
	vector<ll> fact, inv;
	FACTORIAL(int MAX_NUM): fact(MAX_NUM), inv(MAX_NUM) {
		fact[0] = inv[0] = 1;
		for(ll i=1; i<MAX_NUM; i++){
			fact[i] = (fact[i-1] * i) % MOD;
			inv[i] = modinv( fact[i] );
		}
	}
	const ll& operator [ ] ( const int i ) const {
		return fact[i];
	}
} fact(1000006); // n


// (Combination) O(1)
ll cmb(unsigned int n, unsigned int r){
	if( n < r ) return 0;
	return fact[n] * fact.inv[r] % MOD * fact.inv[n-r] % MOD;
}


vector<vector<int>> vec;
void dfs(vector<int> &v, int n, int last=1){
	vec.push_back(v);
	if(last <= 1 && n >= 1){
		auto _v = v;
		_v.push_back(1);
		dfs(_v, n-2, 1);
	}
	if(last <= 2 && n >= 1){
		auto _v = v;
		_v.push_back(2);
		dfs(_v, n-2, 2);
	}
	if( last <= 2 ){
		last = 3;
	}
	for(int i = last; i <= n; i+=2){
		auto _v = v;
		_v.push_back(i);
		dfs(_v, n-i-1, i);
	}
}

