typedef long long ll;
#define ri register int
const int N=75,mod=1e9+7;
int n,k,a[N],use[N],posr[N],posb[N],fac[N],ifac[N],c[N*3][N*3],Ans;
char s[N];
inline int fpow(ri a,ri b){
	ri ans=1;
	for(;b;b>>=1,a=(ll)a*a%mod)
		if(b&1) ans=(ll)ans*a%mod;
	return ans;
}
inline void add(int &x,ri y){
	x=(x+=y)>=mod?x-mod:x; 
}
inline int C(ri n,ri m){
	return m<0||n<m?0:c[n][m];
}
inline void solve(ri cnt){
	ri i,now,res;
	for(i=1;i<=k;++i) use[i]=0;
	for(i=1;i<=cnt;++i) posr[i]=posb[i]=0;
	now=0;
	for(i=1;i<=k&&now<cnt;++i)
		if(s[i]=='r') use[i]=1,posr[++now]=i;
	if(now<cnt) return;
	now=0;
	for(i=1;i<=cnt&&a[i]>=2;++i){
		for(now=max(now,posr[i]+1);now<=k&&s[now]!='b';++now);
		if(now>k) return;
		posb[i]=now;
		use[now]=1;
		++now;
	}
	now=0;
	for(i=1;i<=cnt&&a[i]>=3;++i){
		res=a[i]-2;
		for(now=max(now,posb[i]+1);now<=k&&res;++now)
			if(!use[now])
				use[now]=1,--res;
		if(res) return;
	}

	ri j,ans=fac[cnt],c1,c2;
	for(i=1;i<=cnt;i=j+1){
		for(j=i;j+1<=cnt&&a[j+1]==a[i];++j);
		ans=(ll)ans*ifac[j-i+1]%mod;
	}
	c1=n+1;
	c2=0;
	for(i=1;i<=cnt;++i){
		if(a[i]>=2)
			c1+=2;
		c2+=a[i]*2;
	}
	ans=(ll)ans*C(c1,c2)%mod; 
	add(Ans,ans);
}
inline void dfs(ri i,ri sum){
	if(i>1) solve(i-1);
	for(ri j=a[i-1];j;--j)
		if(sum+j*2-(j>=2?2:0)<=n+1)
			a[i]=j,
			dfs(i+1,sum+j*2-(j>=2?2:0));
}
int main(){
	scanf("%d%d%s",&n,&k,s+1);
	ri i,j,m;
	fac[0]=1;
	for(i=1;i<=n;++i) fac[i]=(ll)fac[i-1]*i%mod;
	ifac[n]=fpow(fac[n],mod-2);
	for(i=n;i;--i) ifac[i-1]=(ll)ifac[i]*i%mod;
	m=3*n+1;
	for(i=0;i<=m;++i){
		c[i][0]=1;
		for(j=1;j<=i;++j)
			c[i][j]=(c[i-1][j-1]+c[i-1][j])%mod;
	}
	a[0]=n;Ans=1;
	dfs(1,0);
	printf("%d\n",Ans);
	return 0;
}

