				if (ned > num)continue;
				sum += t[i] * com(num - ned + add - 1, add - 1);
				sum %= mod;
			}
			//printf(" %lld\n", sum);
			ans = (ans + sum*com(p + q, p)) % mod;
		}
	}
	printf("%lld\n", ans);
}

