{while(x<=k&&(str[x]!='b'||vis[x]))x++;return x;}
int Rs[11111];
bool valid(int T)
{
	if(!T)return 1;
	register int i;
	for(i=1;i<=k+1;i++)vis[i]=0,Rs[i]=0;
	int cc=0;
	for(i=T;i;i--)
		if(siz[i]>1)cc++;
	int x=locatr(1),y=locatb(x);
	for(i=1;i<=cc;i++)
	{
		if(y==k+1)return 0;
		vis[x]=1,vis[y]=1,Rs[y+1]+=siz[i]-2,x=locatr(x+1),y=locatb(y+1);
		if(y<x)y=locatb(x);
	}for(i=1;i<=T;i++)
		if(siz[i]==1)
			if(!eraser())return 0;
	int ss=0,EE=0;
	for(i=k+1;i;i--)
	{
		ss+=Rs[i];
		if(ss>EE)return 0;
		EE+=!vis[i-1];
	}return 1;
}
ll concen(int T)
{
	register int i,ii;
	ll ret=1,G=T;
	for(i=1;i<=T;i=ii+1)
	{
		for(ii=i;ii<T&&siz[ii]==siz[ii+1];ii++);
		(ret*=C[G][ii-i+1])%=mod,G-=ii-i+1;
	}return ret;
}
ll solve(int T,int res)
{
	if(!T)return 1;
	register int i;
	ll SS=T+1;
	for(i=1;i<=T;i++)SS+=2*siz[i]-1;
	return C[SS+res-1][res]*concen(T);
}
